mapper.select:
select
*
from computermanage t 


		 t.depname = #{condition.depname}


		AND t.resperson = #{condition.resperson}


		AND t.devno = #{condition.devno}


		AND t.mac = #{condition.mac}


		AND t.status = #{condition.status}


order by t.updatetime desc
LIMIT #{from}, #{size}

select count(*) count
from computermanage t


		t.depname = #{condition.depname}


		AND t.resperson = #{condition.resperson}


		AND t.devno = #{condition.devno}


		AND t.mac = #{condition.mac}


		AND t.status = #{condition.status}

select
t.*,c.changedate,u.name as applicant,c.bustype,c.changecontent,c.applicationno
from computermanage t,change_history c,user u 


		t.depname = #{condition.depname}


		AND t.resperson = #{condition.resperson}


		AND t.devno = #{condition.devno}


		AND t.mac = #{condition.mac}


		AND t.status = #{condition.status}

	and t.changeid=c.updatekey and c.applicant=u.id

LIMIT #{from}, #{size}

select count(*) count
from computermanage t,change_history c,user u 


		t.depname = #{condition.depname}


		AND t.resperson = #{condition.resperson}


		AND t.devno = #{condition.devno}


		AND t.mac = #{condition.mac}


		AND t.status = #{condition.status}

	and t.changeid=c.updatekey and c.applicant=u.id
