mapper.select:
select t.id,t.region, t.number,t.roomnum,t.infodevno,
t.newassertsno,o.name as resdepart,u.name as resperson,t.devbrand,t.devno,
t.serialno,t.usedate,t.devseculevel,t.usemethod,t.infostatus,t.printhostip,t.printmac,
t.printport,t.patchpanel,t.payhostip,t.payport,t.paymac,t.park,t.status
from internetinfo t ,user u,organization o where t.resperson=u.id and t.resdepart=o.id

	and t.resdepart = #{condition.resdepart}


	and t.resperson = #{condition.resperson}


	and t.devno = #{condition.devno}


	AND t.status = #{condition.status}

order by t.updatetime desc
LIMIT #{from}, #{size}

select count(*) count
from internetinfo t where 1=1

	and t.resdepart = #{condition.resdepart}


	and t.resperson = #{condition.resperson}


	and t.devno = #{condition.devno}


	AND t.status = #{condition.status}

select t.id,t.region, t.number,t.roomnum,t.infodevno, t.newassertsno,o.name as resdepart,u.name as resperson,t.devbrand,t.devno, t.serialno,t.usedate,t.devseculevel,t.usemethod,t.infostatus,t.printhostip,t.printmac, t.printport,t.patchpanel,t.payhostip,t.payport,t.paymac,t.park,t.status,c.changedate,u.name as change_person,c.bustype as bus_type,c.changecontent as change_content,c.applicationno as change_no from internetinfo t,change_history c,user u ,organization o where t.resperson=u.id and t.resdepart=o.id
AND t.devno = #{condition.devno}
AND t.status = #{condition.status}
and t.changeid=c.updatekey and c.applicant=u.id order by t.updatetime desc LIMIT #{from}, #{size}

select count(*) count
from internetinfo t,change_history c,user u where
1=1

	AND t.devno = #{condition.devno}


	AND t.status = #{condition.status}

and t.changeid=c.updatekey and c.applicant=u.id
