mapper.select:
select
t.id,o.name as resdepart,u.name as resperson,t.devno,t.devseclevel,t.brand,t.purpose,t.devproductdate,t.productno,t.hardwareconf,t.diskno,t.mac,t.osinsttime,t.os,t.status
from PlotterInfo t ,user u,organization o where t.resperson=u.id and t.resdepart=o.id

	and t.resdepart = #{condition.resdepart}


	and t.resperson = #{condition.resperson}


	AND t.status = #{condition.status}


order by t.updatetime desc
LIMIT #{from}, #{size}

select count(*) count
from PlotterInfo t where 1=1

	and t.resdepart = #{condition.resdepart}


	and t.resperson = #{condition.resperson}


	AND t.status = #{condition.status}

select t.id,o.name as resdepart,u.name as resperson,t.devno,t.devseclevel,t.brand,t.purpose,t.devproductdate,t.productno,t.hardwareconf,t.diskno,t.mac,t.osinsttime,t.os,t.status,c.changedate,u.name as change_person,c.bustype as bus_type,c.changecontent as change_content,c.applicationno as change_no from PlotterInfo t,change_history c,user u,organization o where t.resperson=u.id and t.resdepart=o.id
AND t.devno = #{condition.devno}
AND t.status = #{condition.status}
and t.changeid=c.updatekey and c.applicant=u.id order by t.updatetime desc LIMIT #{from}, #{size}

select count(*) count
from PlotterInfo t,change_history c,user u where 1=1

	AND t.devno = #{condition.devno}


	AND t.status = #{condition.status}

and t.changeid=c.updatekey and c.applicant=u.id
