mapper.select:
select

from sechostinfo t


		t.usedepart = #{condition.usedepart}


		t.resperson = #{condition.resperson}


		t.model = #{condition.model}


order by t.updatetime desc
LIMIT #{from}, #{size}

select count(*) count
from sechostinfo t


		t.usedepart = #{condition.usedepart}


		t.resperson = #{condition.resperson}


		t.model = #{condition.model}

select
t.*,c.changedate,u.name as
applicant,c.bustype,c.changecontent,c.applicationno
from sechostinfo
t,change_history c,user u



		AND t.mac = #{condition.mac}


		AND t.status = #{condition.status}

	and t.changeid=c.updatekey and c.applicant=u.id

order by t.updatetime desc
LIMIT #{from}, #{size}

select count(*) count
from sechostinfo t,change_history c,user u


		AND t.mac = #{condition.mac}


		AND t.status = #{condition.status}

	and t.changeid=c.updatekey and c.applicant=u.id
