mapper.select:
select
*
from SecPrintInfo t where 1=1

	and t.resdepart = #{condition.resdepart}


	and t.resperson = #{condition.resperson}


	and t.brand = #{condition.brand}


	AND t.status = #{condition.status}

order by t.updatetime desc
LIMIT #{from}, #{size}

select count(*) count
from SecPrintInfo t where 1=1

	and t.resdepart = #{condition.resdepart}


	and t.resperson = #{condition.resperson}


	and t.brand = #{condition.brand}


	AND t.status = #{condition.status}

select t.*,c.changedate,u.name as applicant,c.bustype,c.changecontent,c.applicationno from SecPrintInfo t,change_history c,user u where 1=1
AND t.devno = #{condition.mac}
AND t.status = #{condition.status}
and t.changeid=c.updatekey and c.applicant=u.id order by t.updatetime desc LIMIT #{from}, #{size}

select count(*) count
from SecPrintInfo t,change_history c,user u where 1=1

	AND t.devno = #{condition.mac}


	AND t.status = #{condition.status}

and t.changeid=c.updatekey and c.applicant=u.id
