mapper.select:
select
t.id,t.devno,o.name as depname,u.name as resperson,t.securitylevel,t.newname,t.switchbrand,t.factoryno,t.switchip,t.switchmac,t.location,
t.port,t.patchpanel,t.purpose,t.infostatus,t.region,t.remark,t.status
from secproductinfo t,user u,organization o where t.resperson=u.id and t.depname=o.id

	and t.depname = #{condition.depname}


	and t.resperson = #{condition.resperson}


	AND t.status = #{condition.status}

order by t.updatetime desc
LIMIT #{from}, #{size}

select count(*) count
from secproductinfo t
where 1=1

	and t.depname = #{condition.depname}


	and t.resperson = #{condition.resperson}


	AND t.status = #{condition.status}

select t.id,t.devno,o.name as depname,u.name as resperson,t.securitylevel,t.newname,t.switchbrand,t.factoryno,t.switchip,t.switchmac,t.location, t.port,t.patchpanel,t.purpose,t.infostatus,t.region,t.remark,t.status,c.changedate,u.name as change_person,c.bustype as bus_type,c.changecontent as change_content,c.applicationno as change_no from secproductinfo t,change_history c,user u,organization o where t.resperson=u.id and t.depname=o.id
AND t.devno = #{condition.devno}
AND t.status = #{condition.status}
and t.changeid=c.updatekey and c.applicant=u.id order by t.updatetime desc LIMIT #{from}, #{size}

select count(*) count
from secproductinfo t,change_history c,user u where 1=1

	AND t.devno = #{condition.devno}


	AND t.status = #{condition.status}

and t.changeid=c.updatekey and c.applicant=u.id
