mapper.select:
select
*
from switchinfo t where 1=1

	and t.depname = #{condition.depname}


	and t.resperson = #{condition.resperson}


	and t.devname = #{condition.devname}


	AND t.status = #{condition.status}

order by t.updatetime desc
LIMIT #{from}, #{size}

select count(*) count
from switchinfo t
where 1=1

	and t.depname = #{condition.depname}


	and t.resperson = #{condition.resperson}


	and t.devname = #{condition.devname}


	AND t.status = #{condition.status}

select t.*,c.changedate,u.name as change_person,c.bustype as bus_type,c.changecontent as change_content,c.applicationno as change_no from switchinfo t,change_history c,user u where 1=1
AND t.devno = #{condition.devno}
AND t.status = #{condition.status}
and t.changeid=c.updatekey and c.applicant=u.id order by t.updatetime desc LIMIT #{from}, #{size}

select count(*) count
from switchinfo t,change_history c,user u where 1=1

	AND t.devno = #{condition.devno}


	AND t.status = #{condition.status}

and t.changeid=c.updatekey and c.applicant=u.id
