mapper.select:
select t.id,t.informdevno,o.name as departname,u.name as resperson,t.seclevel,t.assetsno,t.assersown,t.equipmentno,t.devno,t.diskno,t.specification,t.starttime,t.ipaddress,
t.mac,t.location,t.osversion,t.osinstaltime,t.surfcertif,t.usestatus,t.remark,t.status
 from unsechostinfo t,user u,organization o where t.resperson=u.id and t.departname=o.id

	and t.departname = #{condition.departname}


	and t.resperson = #{condition.resperson}


	and t.informdevno = #{condition.informdevno}


	AND t.status = #{condition.status}

order by t.updatetime desc
LIMIT #{from}, #{size}

select count(*) count
from unsechostinfo t
where 1=1

	and t.departname = #{condition.departname}


	and t.resperson = #{condition.resperson}


	and t.informdevno = #{condition.informdevno}


	AND t.status = #{condition.status}

select t.id,t.informdevno,o.name as departname,u.name as resperson,t.seclevel,t.assetsno,t.assersown,t.equipmentno,t.devno,t.diskno,t.specification,t.starttime,t.ipaddress, t.mac,t.location,t.osversion,t.osinstaltime,t.surfcertif,t.usestatus,t.remark,t.status,c.changedate,u.name as change_person,c.bustype as bus_type,c.changecontent as change_content,c.applicationno as change_no from unsechostinfo t,change_history c,user u ,organization o where t.resperson=u.id and t.departname=o.id
AND t.devno = #{condition.devno}
AND t.status = #{condition.status}
and t.changeid=c.updatekey and c.applicant=u.id order by t.updatetime desc LIMIT #{from}, #{size}

select count(*) count
from unsechostinfo t,change_history c,user u where 1=1

	AND t.devno = #{condition.devno}


	AND t.status = #{condition.status}

and t.changeid=c.updatekey and c.applicant=u.id
