mapper.select:
SELECT t.*, c.depname,c.resperson FROM warnusbinfo t,computermanage c WHERE t.mac = c.mac AND t.ip = c.ipaddress AND t.serialNumber = c.diskno
and t.status = #{condition.status}
GROUP BY t.ip, t.mac, t.serialNumber LIMIT #{from}, #{size}

select count(*) count FROM (SELECT t.*, c.depname,c.resperson FROM warnusbinfo t,computermanage c WHERE t.mac = c.mac AND t.ip = c.ipaddress AND t.serialNumber = c.diskno
and t.status = #{condition.status}
GROUP BY t.ip, t.mac, t.serialNumber) a
